
const ieee754 = require("ieee754");

export function intToHexString(int: number, minHexDigits: number): string {
    return ("0".repeat(minHexDigits) + int.toString(16)).substr(-minHexDigits);
}

export function int16ToHexString(int: number): string {
    const buf: Buffer = Buffer.alloc(2);
    buf.writeInt16BE(int, 0);
    return buf.toString('hex');
}

export function floatToHexString(float: number): string {
    let buffer = Buffer.alloc(4);
    let output = "";
    ieee754.write(buffer, float, 0, false, 23, 4);
    for (let i = 0; i < buffer.length; i++) {
        output += ("00" + buffer[i].toString(16)).substr(-2);
    }
    return output;
}

export function hexStringToInt(hexString: string): number {
    return parseInt(hexString, 16);
}

export function hexStringToFloat(hexString: string): number {
    return Buffer.from(hexString, "hex").readFloatBE(0);
}
