export function validateInt(value: any, canBeString: boolean = false): boolean {
    if (typeof value === "number") {
        return (Math.floor(value) === value);
    } else if (canBeString && typeof value === "string") {
        return (parseInt(value).toString() === value);
    }
    return false;
}

export function validateNumber(value: any, canBeString: boolean = false): boolean {
    if (typeof value === "number") {
        return true;
    } else if (canBeString && typeof value === "string") {
        return (parseFloat(value).toString() === value);
    }
    return false;
}
