/**
 * @module RaftError
 */ /** */

declare class Error {
    public message: string;
    public name: string;
    public stack: string;
    constructor(message?: string);
}

/**
 * The base Error used for Raft-related errors.
 */
export default class RaftError extends Error {
    public detail: string;
    constructor(message: string = "") {
        super(message);
        this.name = "RaftError";
        this.message = message;
        this.detail = "";
        this.stack = (<any> new Error(message)).stack;
    }
}
