/**
 * @module RaftError
 */ /** */

import RaftError from "./base";

/**
 * Thrown when a syntax error occurs in instruction parsing.
 */
export default class InstructionSyntaxError extends RaftError {
    public lineNumber: number;
    constructor(message: string, lineNumber: number) {
        super(message);
        this.lineNumber = lineNumber;
        this.name = "InstructionSyntaxError";
    }
}
