/**
 * @module RaftError
 */ /** */

import RaftError from "./base";

/**
 * Thrown when an invalid or insufficient argument is given.
 */
export default class InvalidArgumentError extends RaftError {
    constructor(message?: string) {
        if (message === undefined) {
            message = "Unrecognized command received"
        }
        super(message);
        this.name = "InvalidArgumentError";
    }
}
