/**
 * @module RaftError
 */ /** */

import RaftError from "./base";

/**
 * Thrown when an invalid or insufficient instruction is found.
 */
export default class InvalidInstructionError extends RaftError {

    constructor(message: string = "", instruction: string = "") {
        super(message);
        this.name = "InvalidInstructionError";
        this.detail = instruction;
    }

}
