/**
 * @module RaftInstruction
 */ /** */

export interface RaftInstructionParams {
    comment?: string
}

/**
 * The base class from which all RaftInstructions are derived.
 */
export default class RaftInstruction {

    readonly comment: string;

    constructor (params: RaftInstructionParams) {
        this.comment = params.comment || "";
    }

    toString(): string {
        return "RaftInstruction";
    }

}
