/**
 * @module RaftInstruction
 */ /** */

import RaftInstruction, {RaftInstructionParams} from "./base";

export interface BedStandbyInstructionParams extends RaftInstructionParams {
    exitStandby?: boolean
}

export default class BedStandbyInstruction extends RaftInstruction {

    readonly exitStandby: boolean;

    constructor (params: BedStandbyInstructionParams) {
        super(params);
        this.exitStandby = !!params.exitStandby;
    }

    toString(): string {
        return "BedStandbyInstruction";
    }

}
