/**
 * @module RaftInstruction
 */ /** */

import {validateInt} from "../common/validation";
import InvalidArgumentError from "../errors/invalid-argument";
import RaftInstruction, {RaftInstructionParams} from "./base";

export interface DwellInstructionParams extends RaftInstructionParams {
    duration: number,
    useMilliseconds?: boolean
}

/**
 * Dwell
 * <p>Pause the machine for a period of time (specified in milliseconds by default, or seconds optionally).</p>
 */
export default class DwellInstruction extends RaftInstruction {

    readonly duration: number;
    readonly useMilliseconds: boolean;

    constructor (params: DwellInstructionParams) {
        if (!validateInt(params.duration) || params.duration < 0) {
            throw new InvalidArgumentError("Invalid duration '" + params.duration + "' supplied as argument");
        }
        super(params);
        this.duration = params.duration;
        this.useMilliseconds = (params.useMilliseconds === undefined ? true : !!params.useMilliseconds);
    }

    toString(): string {
        return "DwellInstruction";
    }

}
