import SleepInstruction, {SleepInstructionParams} from "./sleep";

export interface EmergencyStopInstructionParams extends SleepInstructionParams {
}

/**
 * Unconditional Stop
 * <p>Shut down immediately, terminating any moves in progress.</p>
 */
export default class EmergencyStopInstruction extends SleepInstruction {

    constructor (params: EmergencyStopInstructionParams) {
        super(params);
    }

    toString(): string {
        return "EmergencyStopInstruction";
    }

}
