/**
 * @module RaftInstruction
 */ /** */

import RaftInstruction, {RaftInstructionParams} from "./base";

export interface EmptyInstructionParams extends RaftInstructionParams {
}

/**
 * Empty Instruction
 * <p>This represents an instantiable instruction with no properties except comment, inherited from
 * RaftInstruction. This can be used, for example, to store lines containing only comments.</p>
 */
export default class EmptyInstruction extends RaftInstruction {

    constructor (params: EmptyInstructionParams) {
        super(params);
    }

    toString(): string {
        return "EmptyInstruction";
    }

}
