import RaftInstruction, {RaftInstructionParams} from "./base";

export interface FinishBufferInstructionParams extends RaftInstructionParams {
}

/**
 * Wait for current moves to finish
 * <p>Finish any moves left in the buffer, thus clearing it.</p>
 */
export default class FinishBufferInstruction extends RaftInstruction {

    constructor (params: FinishBufferInstructionParams) {
        super(params);
    }

    toString(): string {
        return "FinishBufferInstruction";
    }

}
