/**
 * @module RaftInstruction
 */ /** */

import InvalidArgumentError from "../errors/invalid-argument";
import RaftInstruction, {RaftInstructionParams} from "./base";

export interface LimitAxisTravelInstructionParams extends RaftInstructionParams {
    enabled?: boolean
}

export default class LimitAxisTravelInstruction extends RaftInstruction {

    readonly enabled: boolean;

    constructor (params: LimitAxisTravelInstructionParams) {
        super(params);
        this.enabled = (params.enabled === undefined ? true : !!params.enabled);
    }

    toString(): string {
        return "LimitAxisTravelInstruction";
    }

}
