/**
 * @module RaftInstruction
 */ /** */

import OEMInstruction, {OEMInstructionParams} from "./oem";

export interface OAdjustedPPMInstructionParams extends OEMInstructionParams {
    adjustment: number
}

export default class OAdjustedPPMInstruction extends OEMInstruction {

    readonly adjustment: number;

    constructor (params: OAdjustedPPMInstructionParams) {
        super(params);
        this.adjustment = params.adjustment;
    }

    toString(): string {
        return "OAdjustedPPMInstruction";
    }

}
