/**
 * @module RaftInstruction
 */ /** */

import InvalidArgumentError from "../errors/invalid-argument";
import OCountInstruction, {OCountInstructionParams} from "./o-count";
import {validateNumber} from "../common/validation";

export interface OAlgorithmCountInstructionParams extends OCountInstructionParams {
}

export default class OAlgorithmCountInstruction extends OCountInstruction {

    constructor (params: OAlgorithmCountInstructionParams) {
        if (!validateNumber(params.count) || params.count < 0) {
            throw new InvalidArgumentError("Invalid algorithm count '" + params.count + "' supplied as argument");
        }
        super(params);
    }

    toString(): string {
        return "OAlgorithmCountInstruction";
    }

}
