/**
 * @module RaftInstruction
 */ /** */

import InvalidArgumentError from "../errors/invalid-argument";
import OEMInstruction, {OEMInstructionParams} from "./oem";
import {validateNumber} from "../common/validation";

export interface OCountInstructionParams extends OEMInstructionParams {
    count: number
}

export default abstract class OCountInstruction extends OEMInstruction {

    readonly count: number;

    protected constructor (params: OCountInstructionParams) {
        if (!validateNumber(params.count) || params.count < 0) {
            throw new InvalidArgumentError("Invalid count '" + params.count + "' supplied as argument");
        }
        super(params);
        this.count = params.count;
    }

    toString(): string {
        return "OCountInstruction";
    }

}
