/**
 * @module RaftInstruction
 */ /** */

import InvalidArgumentError from "../errors/invalid-argument";
import OEMInstruction, {OEMInstructionParams} from "./oem";

export interface ODriveInformationInstructionParams extends OEMInstructionParams {
    value: string
}

export default class ODriveInformationInstruction extends OEMInstruction {

    readonly value: string;

    constructor (params: ODriveInformationInstructionParams) {
        if (params.value === "") {
            throw new InvalidArgumentError("No drive information supplied as argument");
        }
        super(params);
        this.value = params.value;
    }

    toString(): string {
        return "ODriveInformationInstruction";
    }

}
