/**
 * @module RaftInstruction
 */ /** */

import InvalidArgumentError from "../errors/invalid-argument";
import OCountInstruction, {OCountInstructionParams} from "./o-count";
import {validateNumber} from "../common/validation";

export interface OHotSwapCountInstructionParams extends OCountInstructionParams {
}

export default class OHotSwapCountInstruction extends OCountInstruction {

    constructor (params: OHotSwapCountInstructionParams) {
        if (!validateNumber(params.count) || params.count < 0) {
            throw new InvalidArgumentError("Invalid hot swap count '" + params.count + "' supplied as argument");
        }
        super(params);
    }

    toString(): string {
        return "OHotSwapCountInstruction";
    }

}
