/**
 * @module RaftInstruction
 */ /** */

import InvalidArgumentError from "../errors/invalid-argument";
import OCountInstruction, {OCountInstructionParams} from "./o-count";
import {validateNumber} from "../common/validation";

export interface OPingCountInstructionParams extends OCountInstructionParams {
}

export default class OPingCountInstruction extends OCountInstruction {

    constructor (params: OPingCountInstructionParams) {
        if (!validateNumber(params.count) || params.count < 0) {
            throw new InvalidArgumentError("Invalid ping count '" + params.count + "' supplied as argument");
        }
        super(params);
    }

    toString(): string {
        return "OPingCountInstruction";
    }

}
