/**
 * @module RaftInstruction
 */ /** */

import InvalidArgumentError from "../errors/invalid-argument";
import OEMInstruction, {OEMInstructionParams} from "./oem";
import {validateNumber} from "../common/validation";

export interface OPingDataInstructionParams extends OEMInstructionParams {
    length: number
    extrusion?: number
}

export default class OPingDataInstruction extends OEMInstruction {

    readonly length: number;
    readonly extrusion: number;

    constructor (params: OPingDataInstructionParams) {
        if (!validateNumber(params.length) || params.length < 0) {
            throw new InvalidArgumentError("Invalid length '" + params.length + "' supplied as argument");
        }
        if (params.extrusion !== undefined && (!validateNumber(params.extrusion) || params.extrusion < 0)) {
            throw new InvalidArgumentError("Invalid extrusion distance '" + params.extrusion + "' supplied as argument");
        }
        super(params);
        this.length = params.length;
        this.extrusion = (params.extrusion === undefined ? null : params.extrusion);
    }

    toString(): string {
        return "OPingDataInstruction";
    }

}
