/**
 * @module RaftInstruction
 */ /** */

import OEMInstruction, {OEMInstructionParams} from "./oem";

export interface OProfileInstructionParams extends OEMInstructionParams {
    id: string
}

export default abstract class OProfileInstruction extends OEMInstruction {

    readonly id: string;

    protected constructor (params: OProfileInstructionParams) {
        super(params);
        this.id = params.id;
    }

    toString(): string {
        return "OProfileInstruction";
    }

}
