/**
 * @module RaftInstruction
 */ /** */

import InvalidArgumentError from "../errors/invalid-argument";
import OCountInstruction, {OCountInstructionParams} from "./o-count";
import {validateNumber} from "../common/validation";

export interface OSpliceCountInstructionParams extends OCountInstructionParams {
}

export default class OSpliceCountInstruction extends OCountInstruction {

    constructor (params: OSpliceCountInstructionParams) {
        if (!validateNumber(params.count) || params.count < 0) {
            throw new InvalidArgumentError("Invalid splice count '" + params.count + "' supplied as argument");
        }
        super(params);
    }

    toString(): string {
        return "OSpliceCountInstruction";
    }

}
