/**
 * @module RaftInstruction
 */ /** */

import InvalidArgumentError from "../errors/invalid-argument";
import OEMInstruction, {OEMInstructionParams} from "./oem";
import {validateNumber} from "../common/validation";

export interface OSpliceDataInstructionParams extends OEMInstructionParams {
    drive: number,
    length: number
}

export default class OSpliceDataInstruction extends OEMInstruction {

    readonly drive: number;
    readonly length: number;

    constructor (params: OSpliceDataInstructionParams) {
        if (!validateNumber(params.drive) || params.drive < 0) {
            throw new InvalidArgumentError("Invalid drive number '" + params.drive + "' supplied as argument");
        }
        if (!validateNumber(params.length) || params.length < 0) {
            throw new InvalidArgumentError("Invalid length '" + params.length + "' supplied as argument");
        }
        super(params);
        this.drive = params.drive;
        this.length = params.length;
    }

    toString(): string {
        return "OSpliceDataInstruction";
    }

}
