/**
 * @module RaftInstruction
 */ /** */

import OEMInstruction, {OEMInstructionParams} from "./oem";
import {validateNumber} from "../common/validation";
import InvalidArgumentError from "../errors/invalid-argument";

export interface OStartMulticolorInstructionParams extends OEMInstructionParams {
    filename: string
    printLength: number
}

export default class OStartMulticolorInstruction extends OEMInstruction {

    readonly filename: string;
    readonly printLength: number;

    constructor (params: OStartMulticolorInstructionParams) {
        if (!validateNumber(params.printLength) || params.printLength < 0) {
            throw new InvalidArgumentError("Invalid length '" + params.printLength + "' supplied as argument");
        }
        super(params);
        this.filename = params.filename;
        this.printLength = params.printLength;
    }

    toString(): string {
        return "OStartMulticolorInstruction";
    }

}
