import RaftInstruction, {RaftInstructionParams} from "./base";

export interface PauseInstructionParams extends RaftInstructionParams {
}

/**
 * GCode-Initiated Pause
 * <p>Initiate a pause in the same way as if pressing the
 * pause button on the machine.</p>
 */
export default class PauseInstruction extends RaftInstruction {

    constructor (params: PauseInstructionParams) {
        super(params);
    }

    toString(): string {
        return "PauseInstruction";
    }

}
