/**
 * @module RaftInstruction
 */ /** */

import {validateInt} from "../common/validation";
import InvalidArgumentError from "../errors/invalid-argument";
import RaftInstruction, {RaftInstructionParams} from "./base";

export interface PlayBeepInstructionParams extends RaftInstructionParams {
    frequency: number,
    duration: number
}

/**
 * Play Beep Sound
 * <p>Play a beep sound, with frequency in Hz and duration in milliseconds.</p>
 */
export default class PlayBeepInstruction extends RaftInstruction {

    readonly frequency: number;
    readonly duration: number;

    constructor (params: PlayBeepInstructionParams) {
        if (!validateInt(params.frequency) || params.frequency < 0) {
            throw new InvalidArgumentError("Invalid frequency value '" + params.frequency + "' supplied as argument");
        }
        if (!validateInt(params.duration) || params.duration < 0) {
            throw new InvalidArgumentError("Invalid duration '" + params.duration + "' supplied as argument");
        }
        super(params);
        this.frequency = params.frequency;
        this.duration = params.duration;
    }

    toString(): string {
        return "PlayBeepInstruction";
    }

}
