/**
 * @module RaftInstruction
 */ /** */

import {validateInt} from "../common/validation";
import InvalidArgumentError from "../errors/invalid-argument";
import RaftInstruction, {RaftInstructionParams} from "./base";

export interface PlaySongInstructionParams extends RaftInstructionParams {
    id: number
}

/**
 * Play a Tone or Song
 * <p>Instruct the machine to play a preset song using an acceptable song ID.</p>
 */
export default class PlaySongInstruction extends RaftInstruction {

    readonly id: number;

    constructor (params: PlaySongInstructionParams) {
        if (!validateInt(params.id) || params.id < 0) {
            throw new InvalidArgumentError("Invalid song ID '" + params.id + "' supplied as argument");
        }
        super(params);
        this.id = params.id;
    }

    toString(): string {
        return "PlaySongInstruction";
    }

}
