import {validateInt} from "../common/validation";
import InvalidArgumentError from "../errors/invalid-argument";
import RaftInstruction, {RaftInstructionParams} from "./base";

export interface RetractInstructionParams extends RaftInstructionParams {
    isUnretract?: boolean
}

export default class RetractInstruction extends RaftInstruction {

    readonly isUnretract: boolean;

    constructor (params: RetractInstructionParams) {
        super(params);
        this.isUnretract = !!params.isUnretract;
    }

    toString(): string {
        return "RetractInstruction";
    }

}
