/**
 * @module RaftInstruction
 */ /** */

import InvalidArgumentError from "../errors/invalid-argument";
import RaftInstruction, {RaftInstructionParams} from "./base";

export interface SetEndstopDetectionInstructionParams extends RaftInstructionParams {
    enabled: boolean
}

export default class SetEndstopDetectionInstruction extends RaftInstruction {

    readonly enabled: boolean;

    constructor (params: SetEndstopDetectionInstructionParams) {
        if (params.enabled === undefined) {
            throw new InvalidArgumentError("Invalid enabled flag '" + params.enabled + "' supplied as argument");
        }
        super(params);
        this.enabled = !!params.enabled;
    }

    toString(): string {
        return "SetEndstopDetectionInstruction";
    }

}
