/**
 * @module RaftInstruction
 */ /** */

import InvalidArgumentError from "../errors/invalid-argument";
import SetMaxAccelerationInstruction, {SetMaxAccelerationInstructionParams} from "./set-max-acceleration";

export interface SetMaxTravelAccelerationInstructionParams extends SetMaxAccelerationInstructionParams {
}

export default class SetMaxTravelAccelerationInstruction extends SetMaxAccelerationInstruction {

    constructor (params: SetMaxTravelAccelerationInstructionParams) {
        super(params);
        if (params.extrusion !== undefined) {
            throw new InvalidArgumentError("Unexpected 'extrusion' parameter supplied as argument");
        }
    }

    toString(): string {
        return "SetMaxTravelAccelerationInstruction";
    }

}
