/**
 * @module RaftInstruction
 */ /** */

import {validateNumber} from "../common/validation";
import InvalidArgumentError from "../errors/invalid-argument";
import RaftInstruction, {RaftInstructionParams} from "./base";

export interface SetOverrideFactorInstructionParams extends RaftInstructionParams {
    percentage: number
}

export default class SetOverrideFactorInstruction extends RaftInstruction {

    readonly percentage: number;

    constructor (params: SetOverrideFactorInstructionParams) {
        if (!validateNumber(params.percentage) || params.percentage < 0 || params.percentage > 100) {
            throw new InvalidArgumentError("Invalid percentage value '" + params.percentage + "' supplied as argument");
        }
        super(params);
        this.percentage = params.percentage;
    }

    toString(): string {
        return "SetOverrideFactorInstruction";
    }

}
