/**
 * @module RaftInstruction
 */ /** */

import {validateInt} from "../common/validation";
import InvalidArgumentError from "../errors/invalid-argument";
import RaftInstruction, {RaftInstructionParams} from "./base";

export interface SetPIDSampleIntervalInstructionParams extends RaftInstructionParams {
    interval: number
}

export default class SetPIDSampleIntervalInstruction extends RaftInstruction {

    readonly interval: number;

    constructor (params: SetPIDSampleIntervalInstructionParams) {
        if (!validateInt(params.interval) || params.interval <= 0) {
            throw new InvalidArgumentError("Invalid interval '" + params.interval + "' supplied as argument");
        }
        super(params);
        this.interval = params.interval;
    }

    toString(): string {
        return "SetPIDSampleIntervalInstruction";
    }

}
