/**
 * @module RaftInstruction
 */ /** */

import InvalidArgumentError from "../errors/invalid-argument";
import OffsetAxisInstruction, {OffsetAxisInstructionParams} from "./offset-axis";
import {validateInt} from "../common/validation";

interface SetToolOffsetInstructionParams extends OffsetAxisInstructionParams {
    toolhead: number,
    x?: number,
    y?: number,
    z?: number,
}

export default class SetToolOffsetInstruction extends OffsetAxisInstruction {

    readonly toolhead: number;

    constructor (params: SetToolOffsetInstructionParams) {
        if (params.toolhead !== null && !validateInt(params.toolhead) || params.toolhead < 0) {
            throw new InvalidArgumentError("Invalid toolhead '" + params.toolhead + "' supplied as argument");
        }
        super(params);
        this.toolhead = params.toolhead;
    }

    toString(): string {
        return "SetToolOffsetInstruction";
    }

}
