/**
 * @module RaftInstruction
 */ /** */

import InvalidArgumentError from "../errors/invalid-argument";
import SetTemperatureInstruction, {SetTemperatureInstructionParams} from "./set-temperature";
import {validateInt} from "../common/validation";

interface SetToolheadTemperatureInstructionParams extends SetTemperatureInstructionParams {
    toolhead?: number
}

export default class SetToolheadTemperatureInstruction extends SetTemperatureInstruction {

    readonly toolhead: number;

    constructor (params: SetToolheadTemperatureInstructionParams) {
        if (params.toolhead !== undefined && (!validateInt(params.toolhead) || params.toolhead < 0)) {
            throw new InvalidArgumentError("Invalid toolhead '" + params.toolhead + "' supplied as argument");
        }
        super(params);
        this.toolhead = (params.toolhead === undefined ? null : params.toolhead);
    }

    toString(): string {
        return "SetToolheadTemperatureInstruction";
    }

}
