/**
 * @module RaftInstruction
 */ /** */

import RaftInstruction, {RaftInstructionParams} from "./base";
import {DistanceUnits} from "../enums";
import InvalidArgumentError from "../errors/invalid-argument";

export interface SetUnitsInstructionParams extends RaftInstructionParams {
    units?: DistanceUnits
}

export default class SetUnitsInstruction extends RaftInstruction {

    readonly units: DistanceUnits;

    constructor (params: SetUnitsInstructionParams) {
        if (params.units !== undefined && !(params.units in DistanceUnits)) {
            throw new InvalidArgumentError("Invalid units value '" + params.units + "' not in DistanceUnits");
        }
        super(params);
        this.units = params.units || DistanceUnits.Millimeters
    }

    toString(): string {
        return "SetUnitsInstruction";
    }

}
