/**
 * @module RaftInstruction
 */ /** */

import InvalidArgumentError from "../errors/invalid-argument";
import StabilizeTemperatureInstruction, {StabilizeTemperatureInstructionParams} from "./stabilize-temperature";
import {validateInt, validateNumber} from "../common/validation";

interface StabilizeBedTemperatureInstructionParams extends StabilizeTemperatureInstructionParams {
    bedNumber?: number
}

export default class StabilizeBedTemperatureInstruction extends StabilizeTemperatureInstruction {

    readonly bedNumber: number;

    constructor (params: StabilizeBedTemperatureInstructionParams) {
        if (params.bedNumber !== undefined && (!validateInt(params.bedNumber) || params.bedNumber < 0)) {
            throw new InvalidArgumentError("Invalid bedNumber '" + params.bedNumber + "' supplied as argument");
        }
        super(params);
        this.bedNumber = (params.bedNumber === undefined ? null : params.bedNumber);
    }

    toString(): string {
        return "StabilizeBedTemperatureInstruction";
    }

}
