/**
 * @module RaftInstruction
 */ /** */

import InvalidArgumentError from "../errors/invalid-argument";
import RaftInstruction, {RaftInstructionParams} from "./base";
import {validateNumber} from "../common/validation";

export interface StabilizeTemperatureInstructionParams extends RaftInstructionParams {
    stabilizeTimeout?: number
}

export default class StabilizeTemperatureInstruction extends RaftInstruction {

    readonly stabilizeTimeout: number;

    constructor (params: StabilizeTemperatureInstructionParams) {
        if (params.stabilizeTimeout !== undefined && (!validateNumber(params.stabilizeTimeout) || params.stabilizeTimeout < 0)) {
            throw new InvalidArgumentError("Invalid stabilizeTimeout '" + params.stabilizeTimeout + "' supplied as argument");
        }
        super(params);
        this.stabilizeTimeout = (params.stabilizeTimeout === undefined ? null : params.stabilizeTimeout);
    }

    toString(): string {
        return "StabilizeTemperatureInstruction";
    }

}
