/**
 * @module RaftInstruction
 */ /** */

import InvalidArgumentError from "../errors/invalid-argument";
import StabilizeTemperatureInstruction, {StabilizeTemperatureInstructionParams} from "./stabilize-temperature";
import {validateInt} from "../common/validation";

interface StabilizeToolheadTemperatureInstructionParams extends StabilizeTemperatureInstructionParams {
    toolhead: number
}

export default class StabilizeToolheadTemperatureInstruction extends StabilizeTemperatureInstruction {

    readonly toolhead: number;

    constructor (params: StabilizeToolheadTemperatureInstructionParams) {
        if (!validateInt(params.toolhead) || params.toolhead < 0) {
            throw new InvalidArgumentError("Invalid toolhead '" + params.toolhead + "' supplied as argument");
        }
        super(params);
        this.toolhead = params.toolhead;
    }

    toString(): string {
        return "StabilizeToolheadTemperatureInstruction";
    }

}
