/**
 * @module RaftInstruction
 */ /** */

import InvalidArgumentError from "../errors/invalid-argument";
import RaftInstruction, {RaftInstructionParams} from "./base";
import {validateNumber} from "../common/validation";

export interface StopIdleHoldInstructionParams extends RaftInstructionParams {
    timeout?: number
}

export default class StopIdleHoldInstruction extends RaftInstruction {

    readonly timeout: number;

    constructor (params: StopIdleHoldInstructionParams) {
        if (params.timeout !== undefined && (!validateNumber(params.timeout) || params.timeout < 0)) {
            throw new InvalidArgumentError("Invalid timeout '" + params.timeout + "' supplied as argument");
        }
        super(params);
        this.timeout = (params.timeout === undefined ? null : params.timeout);
    }

    toString(): string {
        return "StopIdleHoldInstruction";
    }

}
