/**
 * @module RaftInstruction
 */ /** */

import RaftInstruction, {RaftInstructionParams} from "./base";

export interface UnknownInstructionParams extends RaftInstructionParams {
    original: string
}

/**
 * Unknown Instruction
 * <p>This represents a non-empty instruction that could not be translated into a RaftInstruction.</p>
 */
export default class UnknownInstruction extends RaftInstruction {

    readonly original: string;

    constructor (params: UnknownInstructionParams) {
        super(params);
        this.original = params.original;
    }

    toString(): string {
        return "UnknownInstruction";
    }

}
