/**
 * @module RaftParser
 */ /** */

import BinaryGCodeParser from "./binary-gcode";
import RaftEngine from "../engines/base";

export default class GXParser extends BinaryGCodeParser {

    constructor (engine: RaftEngine) {
        super(engine);
    }

    public clone(): GXParser {
        return new GXParser(this.engine.clone());
    }

    public readFile(inpath: string): string|Buffer {
        return super.readFile(inpath);
    }

    public writeFile(outpath: string, contents: string|Buffer): void {
        super.writeFile(outpath, contents);
    }

    toString(): string {
        return "GXParser";
    }

}
